import Axios from "axios";

export function fetch(route: string) {
    return Axios({
        method: "GET",
        url: `${process.env.API_URL}${route}`,
        headers: {
            "Content-Type": "application/json"
        }
    })
        .then(res => res.data)
        .catch(err => console.log(err));
}

export function post(route: string, data: any) {
    return Axios({
        method: "POST",
        url: ""
    })
}
